import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./HeaderFooter.css";

export default function Header() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token") || sessionStorage.getItem("token");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");   // ✅ Remove user info too
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("user"); // ✅ Remove user info too
    alert("Logged out!");
    navigate("/");
  };
  
  const handleSearch = () => {
    const searchTerm = query.trim() !== "" ? query : category;
    fetchBooks(searchTerm);
  };

  return (
   
    <header className="header">
     
      <div className="header-with-icon">
      <img  src="/images/book.png" alt="Book Icon" className="header-icon" />
      <h1>Online Bookshop </h1>
      </div>
      <nav className="nav-links">
        <Link to="/">Home</Link>
        <Link to="/cart">Cart</Link>
        {token ? (
          <>
            <Link to="/profile">Profile</Link>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
    </header>
  );
}
