import React, { useEffect, useState } from "react";
import axios from "axios";
import BookCard from "../components/BookCard";
import { useLocation } from "react-router-dom";
import "./Home.css";

export default function Home() {
  const [books, setBooks] = useState([]);
  const [category, setCategory] = useState("all");
  const [currentIndex, setCurrentIndex] = useState(0);

  const location = useLocation();

  const getSearchTerm = () => {
    const params = new URLSearchParams(location.search);
    return params.get("q") || category;
  };

  const fetchBooks = (searchTerm) => {
    axios
      .get(`https://openlibrary.org/search.json?q=${searchTerm}`)
      .then((res) => setBooks(res.data.docs.slice(0, 20)))
      .catch(() => console.error("Failed to fetch books"));
  };

  useEffect(() => {
    fetchBooks(getSearchTerm());
  }, [location.search, category]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % 3);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleCategoryClick = (cat) => {
    setCategory(cat);
  };

  return (
    <div className="home-container">
      {/* Carousel */}
      <div className="carousel">
        <div className={`carousel-item ${currentIndex === 0 ? "active" : ""}`}>
          <p>
            IT'S CHAPTERONE<br />
            <h1>We Love Literature</h1>
            We Love Literature Lorem ipsum dolor sit amet, <br />
            consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore megna <br />
            <button className="btn1">READ MORE</button>
          </p>
        </div>
        <div className={`carousel-item ${currentIndex === 1 ? "active" : ""}`}>
          <p>
            IT'S CHAPTERONE<br />
            <h1>Your world of words</h1>
            Your world of words Lorem ipsum dolor sit amet, consectetur<br />
            adipisicing elit, sed do eiusmod tempor incididunt ut<br />
            labore et dolore megna<br />
            <button className="btn1">READ MORE</button>
          </p>
        </div>
        <div className={`carousel-item ${currentIndex === 2 ? "active" : ""}`}>
          <p>
            IT'S CHAPTERONE<br />
            <h1>The all-time classics</h1>
            The all-time classics Lorem ipsum dolor <br />
            sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore megna<br />
            <button className="btn1">READ MORE</button>
          </p>
        </div>
        <div className="carousel-dots">
          {[0, 1, 2].map((index) => (
            <span
              key={index}
              className={`dot ${currentIndex === index ? "active" : ""}`}
            ></span>
          ))}
        </div>
      </div>

      <br />
      <div className="content-column">
        <div className="category-filters">
          {["all", "best sellers", "fantasy", "history", "art", "love stories"].map((cat) => (
            <button
              key={cat}
              className={category === cat ? "active" : ""}
              onClick={() => handleCategoryClick(cat)}
            >
              {cat.charAt(0).toUpperCase() + cat.slice(1)}
            </button>
          ))}
        </div>

        <div className="book-grid">
          {books.map((book) => (
            <BookCard key={book.key} book={book} />
          ))}
        </div>
      </div>
    </div>
  );
}
