import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./HeaderFooter.css";

export default function Header() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token") || sessionStorage.getItem("token");
  const [books, setBooks] = useState([]);
    const [query, setQuery] = useState("");
    
  
    const fetchBooks = (searchTerm) => {
      axios
        .get(`https://openlibrary.org/search.json?q=${searchTerm}`)
        .then((res) => setBooks(res.data.docs.slice(0, 20)))
        .catch(() => console.error("Failed to fetch books"));
    };
  
    useEffect(() => {
      fetchBooks(category);
    }, [category]);
  
    const handleSearch = () => {
      const searchTerm = query.trim() !== "" ? query : category;
      fetchBooks(searchTerm);
    };
  
    

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");   // ✅ Remove user info too
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("user"); // ✅ Remove user info too
    alert("Logged out!");
    navigate("/");
  };
  
  

  return (
   
    <header className="header">
     
      <div className="header-with-icon">
      <img  src="/images/book.png" alt="Book Icon" className="header-icon" />
      <h1>Online Bookshop </h1>
      </div>
      <div className="search-bar">
          <input
            type="text"
            placeholder="Search books..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>
      <nav className="nav-links">
        <Link to="/">Home</Link>
        <Link to="/cart">Cart</Link>
        {token ? (
          <>
            <Link to="/profile">Profile</Link>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
    </header>
  );
}
