import React, { useEffect, useState } from "react";

import "./Cart.css";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(storedCart);
  }, []);

  const handleRemoveItem = (id) => {
    const updatedCart = cartItems.filter((item) => item.id !== id);
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleQuantityChange = (id, newQuantity) => {
    if (newQuantity < 1) return; // Don't allow 0 or negative quantity
    const updatedCart = cartItems.map((item) =>
      item.id === id ? { ...item, quantity: newQuantity } : item
    );
    setCartItems(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const totalPrice = cartItems.reduce(
    (total, item) => total + parseFloat(item.price) * item.quantity,
    0
  );

  const handleCheckout = () => {
    const user = localStorage.getItem("user") || sessionStorage.getItem("user"); // ðŸ‘ˆ check both
  
    if (!user) {
      alert("Please login first to checkout.");
      navigate("/login"); // redirect to login
      return;
    }
  
    if (cartItems.length === 0) {
      alert("Your cart is empty!");
      return;
    }
  
    const confirmCheckout = window.confirm(`Your total is Rs. ${totalPrice.toFixed(2)}. Do you want to proceed to checkout?`);
  
    if (confirmCheckout) {
      localStorage.removeItem("cart"); // Clear cart
      setCartItems([]);
      alert("Purchase completed successfully! ðŸŽ‰");
    }
  };
  

  if (cartItems.length === 0) {
    return <div className="cart"><h2>Your cart is empty</h2></div>;
  }

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cartItems.map((item, index) => (
          <div className="cart-item" key={index}>
            <img src={item.coverImageUrl} alt={item.title} />
            <div className="item-info">
              <h3>{item.title}</h3>
              <p>Price: Rs. {item.price}</p>
              <div className="quantity-controls">
                <button onClick={() => handleQuantityChange(item.id, item.quantity - 1)}>-</button>
                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                />
                <button onClick={() => handleQuantityChange(item.id, item.quantity + 1)}>+</button>
              </div>
              <p>Subtotal: Rs. {(item.price * item.quantity).toFixed(2)}</p>
              <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
            </div>
          </div>
        ))}
      </div>
      <div className="cart-summary">
        <h3>Total: Rs. {totalPrice.toFixed(2)}</h3>
        <button className="checkout-btn" onClick={handleCheckout}>
          Checkout
        </button>
      </div>
    </div>
  );
}
