import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./BookDetails.css";

export default function BookDetails() {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [coverId, setCoverId] = useState(null);

  useEffect(() => {
    // Fetch book details from /works
    axios
      .get(`https://openlibrary.org/works/${id}.json`)
      .then((res) => {
        setBook(res.data);
      })
      .catch((err) => console.error("Failed to load book details", err));

    // Fetch cover_id from editions
    axios
      .get(`https://openlibrary.org/works/${id}/editions.json?limit=1`)
      .then((res) => {
        const firstEdition = res.data.entries[0];
        if (firstEdition?.covers && firstEdition.covers.length > 0) {
          setCoverId(firstEdition.covers[0]);
        }
      })
      .catch((err) => console.error("Failed to load cover image", err));
  }, [id]);

  if (!book) return <p>Loading...</p>;

  const coverImageUrl = coverId
    ? `https://covers.openlibrary.org/b/id/${coverId}-L.jpg`
    : "/default-book-cover.jpg"; // fallback image

  const description =
    typeof book.description === "string"
      ? book.description
      : book.description?.value || "No description available.";

  return (
    <div className="book-details">
      <div className="book-cover">
        <img src={coverImageUrl} alt={book.title} />
      </div>
      <div className="book-info">
        <h2>{book.title}</h2>
        <p><strong>Description:</strong> {description}</p>
        <p><strong>Price:</strong> Rs. {(Math.random() * 1000).toFixed(2)}</p>
        <p><strong>Stock:</strong> In stock</p>
      </div>
    </div>
  );
}
