import React, { useEffect, useState } from "react";
import axios from "axios";
import BookCard from "../components/BookCard";
import { FaSearch, FaHeart, FaStar, FaBookOpen } from "react-icons/fa";
import "./Home.css";

export default function Home() {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState("all");
  const [currentIndex, setCurrentIndex] = useState(0);

  const fetchBooks = (searchTerm) => {
    axios
      .get(`https://openlibrary.org/search.json?q=${searchTerm}`)
      .then((res) => setBooks(res.data.docs.slice(0, 20)))
      .catch(() => console.error("Failed to fetch books"));
  };

  useEffect(() => {
    fetchBooks(category);
  }, [category]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % 3);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleSearch = () => {
    const searchTerm = query.trim() !== "" ? query : category;
    fetchBooks(searchTerm);
  };

  const handleCategoryClick = (cat) => {
    setCategory(cat);
    setQuery("");
    fetchBooks(cat);
  };

  return (
    <div className="home-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search books..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>
          <FaSearch /> Search
        </button>
      </div>

      {/* Carousel */}
      <div className="carousel">
        <div className={`carousel-item ${currentIndex === 0 ? "active" : ""}`}>
          <p>
            IT'S CHAPTERONE <br />
            <h1>We Love Literature</h1>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. <br />
            <button className="btn1">READ MORE</button>
          </p>
        </div>
        <div className={`carousel-item ${currentIndex === 1 ? "active" : ""}`}>
          <p>
            IT'S CHAPTERONE <br />
            <h1>Your World of Words</h1>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. <br />
            <button className="btn1">READ MORE</button>
          </p>
        </div>
        <div className={`carousel-item ${currentIndex === 2 ? "active" : ""}`}>
          <p>
            IT'S CHAPTERONE <br />
            <h1>The All-Time Classics</h1>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. <br />
            <button className="btn1">READ MORE</button>
          </p>
        </div>
        <div className="carousel-dots">
          {[0, 1, 2].map((index) => (
            <span
              key={index}
              className={`dot ${currentIndex === index ? "active" : ""}`}
            ></span>
          ))}
        </div>
      </div>

      <div className="content-column">
        <div className="category-filters">
          {[
            { label: "All", icon: <FaBookOpen /> },
            { label: "Best Sellers", icon: <FaStar /> },
            { label: "Fantasy", icon: <FaHeart /> },
            { label: "History", icon: <FaBookOpen /> },
            { label: "Art", icon: <FaHeart /> },
            { label: "Love Stories", icon: <FaStar /> },
          ].map((cat) => (
            <button
              key={cat.label}
              className={category === cat.label.toLowerCase() ? "active" : ""}
              onClick={() => handleCategoryClick(cat.label.toLowerCase())}
            >
              {cat.icon} {cat.label}
            </button>
          ))}
        </div>

        <div className="book-grid">
          {books.map((book) => (
            <BookCard key={book.key} book={book} />
          ))}
        </div>
      </div>
    </div>
  );
}
