import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./BookDetails.css";

export default function BookDetails() {
  const { id } = useParams();  // Get the book ID from the URL
  const [book, setBook] = useState(null);

  useEffect(() => {
    axios
      .get(`https://openlibrary.org/works/${id}.json`)
      .then((res) => setBook(res.data))
      .catch((err) => console.error("Failed to load book details", err));
  }, [id]);

  if (!book) return <p>Loading...</p>;

  // Extract the cover image URL (if available)
  const coverUrl = book.cover_id
    ? `https://covers.openlibrary.org/b/id/${book.cover_id}-L.jpg`
    : "default-book-cover.jpg"; // Fallback to a default cover if not available

  return (
    <div className="book-details">
      <div className="book-cover">
        <img src={coverUrl} alt={book.title} />
      </div>
      <div className="book-info">
        <h2>{book.title}</h2>
        <p><strong>Description:</strong> {book.description?.value || "No description."}</p>
        <p><strong>Price:</strong> Rs. {(Math.random() * 1000).toFixed(2)}</p>
        <p><strong>Stock:</strong> In stock</p>
      </div>
    </div>
  );
}
