import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Profile.css";

export default function Profile() {
  const [user, setUser] = useState(null);
  const [edit, setEdit] = useState(false);
  const [form, setForm] = useState({ username: "", age: "", bio: "" });
  const [file, setFile] = useState(null);
  const [orders, setOrders] = useState([]); // ðŸ‘ˆ new state for orders
  const [error, setError] = useState("");

  const token = localStorage.getItem("token") || sessionStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      setError("User not logged in.");
      return;
    }

    // Fetch profile
    axios
      .get("http://localhost:5000/api/profile", {
        headers: { Authorization: token },
      })
      .then((res) => {
        setUser(res.data);
        setForm({
          username: res.data.username,
          age: res.data.age,
          bio: res.data.bio,
        });
      })
      .catch((err) => {
        setError("Failed to load profile.");
        console.error(err);
      });

    // Fetch order history ðŸ‘‡
    axios
      .get("http://localhost:5000/api/orders", {
        headers: { Authorization: token },
      })
      .then((res) => {
        setOrders(res.data);
      })
      .catch((err) => {
        console.error("Failed to load orders", err);
      });
  }, [token]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("username", form.username);
    formData.append("age", form.age);
    formData.append("bio", form.bio);
    if (file) formData.append("profileImage", file);

    try {
      const res = await axios.put("http://localhost:5000/api/profile", formData, {
        headers: {
          Authorization: token,
          "Content-Type": "multipart/form-data",
        },
      });
      setUser(res.data);
      setEdit(false);
      alert("Profile updated!");
    } catch (err) {
      console.error(err);
      setError("Failed to update profile");
    }
  };

  if (error) return <div className="profile-container"><p className="error">{error}</p></div>;
  if (!user) return <div className="profile-container"><p>Loading...</p></div>;

  return (
    <div className="profile-container">
      <h2>{user.username}</h2>

      {user.profileImage && (
        <img
          src={`http://localhost:5000/uploads/${user.profileImage}`}
          alt="Profile"
          className="profile-pic"
        />
      )}

      {!edit ? (
        <>
          <div className="profile-card">
            <p><strong>Username:</strong> {user.username}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Age:</strong> {user.age || "Not specified"}</p>
            <p><strong>Bio:</strong> {user.bio || "No bio available."}</p>
          </div>
          <button className="btn" onClick={() => setEdit(true)}>Edit Profile</button>

          {/* --- Order History Section --- */}
          <div className="order-history">
            <h3>Order History</h3>
            {orders.length === 0 ? (
              <p>No orders found.</p>
            ) : (
              <ul className="order-list">
                {orders.map((order) => (
                  <li key={order._id} className="order-item">
                    <p><strong>Order ID:</strong> {order._id}</p>
                    <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
                    <p><strong>Status:</strong> {order.status}</p>
                    <p><strong>Total:</strong> ${order.total}</p>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </>
      ) : (
        <form onSubmit={handleUpdate} className="edit-form">
          <input
            value={form.username}
            onChange={(e) => setForm({ ...form, username: e.target.value })}
            placeholder="Username"
          />
          <input
            type="number"
            value={form.age}
            onChange={(e) => setForm({ ...form, age: e.target.value })}
            placeholder="Age"
          />
          <textarea
            value={form.bio}
            onChange={(e) => setForm({ ...form, bio: e.target.value })}
            placeholder="Bio"
          ></textarea>
          <input type="file" accept="image/*" onChange={(e) => setFile(e.target.files[0])} />
          <button type="submit" className="btn">Save</button>
          <button type="button" className="btn cancel-btn" onClick={() => setEdit(false)}>Cancel</button>
        </form>
      )}
    </div>
  );
}
